/**
 * Retuns the number of enemies in the game
 * @param (number) n - Number of players
 */
let chunguitosF = function(n) {
    switch(n) {
        case 5:
        case 6:
            return 2;
        case 7:
        case 8:
        case 9:
            return 3;
        case 10:
            return 4;
        default:
            throw "The number of players is not valid";
    }
}

let players = msg.payload; // Array of players
let chunguitos = chunguitosF(players.length); // number of chunguitos
msg.chunguitos = []; // All the chunguitos pIds will be stored here

let indices = [];
while (indices.length < chunguitos) {
    let alreadyIn = false;
    let newRandom = Math.floor(Math.random() * players.length); // Not +1 because this will be used for array indexing
    for (let i of indices) {
        if (i == newRandom) {
            alreadyIn = true;
            break;
        }
    }
    if (!alreadyIn) {
        indices.push(newRandom);
        try {

            msg.chunguitos.push(players[newRandom].pId);
        }
        catch(e) {
            node.warn("ERROR on pId: index = " + newRandom);
            throw e;
        }
    }
}
